class DBHelper{static get DATABASE_URL(){return"http://localhost:1337"}static fetchRestaurants(t){getFromRestaurantOS().then(e=>{t(null,e)}).catch(e=>{fetch(`${DBHelper.DATABASE_URL}/restaurants`).then(t=>t.json()).then(e=>{addToRestaurantOS(e),t(null,e)}).catch(e=>{t(`Request failed. Returned status of ${e}`,null)})})}static fetchRestaurantById(t,e){DBHelper.fetchRestaurants((a,l)=>{if(a)e(a,null);else{const a=l.find(e=>e.id==t);a?e(null,a):e("Restaurant does not exist",null)}})}static fetchRestaurantByCuisine(t,e){DBHelper.fetchRestaurants((a,l)=>{if(a)e(a,null);else{const a=l.filter(e=>e.cuisine_type==t);e(null,a)}})}static fetchRestaurantByNeighborhood(t,e){DBHelper.fetchRestaurants((a,l)=>{if(a)e(a,null);else{const a=l.filter(e=>e.neighborhood==t);e(null,a)}})}static fetchRestaurantByCuisineAndNeighborhood(t,e,a){DBHelper.fetchRestaurants((l,n)=>{if(l)a(l,null);else{let l=n;"all"!=t&&(l=l.filter(e=>e.cuisine_type==t)),"all"!=e&&(l=l.filter(t=>t.neighborhood==e)),a(null,l)}})}static fetchNeighborhoods(t){DBHelper.fetchRestaurants((e,a)=>{if(e)t(e,null);else{const e=a.map((t,e)=>a[e].neighborhood),l=e.filter((t,a)=>e.indexOf(t)==a);t(null,l)}})}static fetchCuisines(t){DBHelper.fetchRestaurants((e,a)=>{if(e)t(e,null);else{const e=a.map((t,e)=>a[e].cuisine_type),l=e.filter((t,a)=>e.indexOf(t)==a);t(null,l)}})}static urlForRestaurant(t){return`./restaurant.html?id=${t.id}`}static imageUrlForRestaurant(t){return 10===t.id?"/dist/img/10.jpg":`/dist/img/${t.photograph}.jpg`}static mapMarkerForRestaurant(t,e){const a=new L.marker([t.latlng.lat,t.latlng.lng],{title:t.name,alt:t.name,url:DBHelper.urlForRestaurant(t)});return a.addTo(newMap),a}}