const dbPromise=idb.open("restaurantDb",1,t=>{t.createObjectStore("restaurants")});function addToRestaurantOS(t){dbPromise.then(e=>{const r=e.transaction("restaurants","readwrite");return r.objectStore("restaurants").put(t,"restaurants"),r.complete}).then(()=>{console.log("added Items to the  store :) ")}).catch(t=>{console.log(t)})}function getFromRestaurantOS(){return new Promise(function(t,e){dbPromise.then(t=>{return t.transaction("restaurants","readwrite").objectStore("restaurants").get("restaurants")}).then(r=>{r?t(r):e("no data"),console.log(" done successfuly !")}).catch(t=>{e("no data"),console.log(t)})})}