const dbPromise=idb.open("restaurantDb",2,e=>{switch(e.oldVersion){case 0:e.createObjectStore("restaurants");case 1:e.createObjectStore("reviews",{keyPath:"id"}).createIndex("restaurant","restaurant_id")}});function addToReviewsOS(e){dbPromise.then(t=>{const r=t.transaction("reviews","readwrite"),o=r.objectStore("reviews");return Array.isArray(e)?e.forEach(e=>{o.put(e)}):o.put(e,"reviews"),r.complete}).then(()=>{console.log("added Items to review  store :) ")}).catch(e=>{console.log(e)})}function getFromReviewsOS(e){return new Promise(function(t,r){dbPromise.then(t=>{const r=t.transaction("reviews","readwrite").objectStore("reviews").index("restaurant").getAll(e);return console.log("get items from store .."),r}).then(e=>{e?(console.log("get items from store .."),t(e)):r("no reviews"),console.log(e)}).catch(e=>{r("no data"),console.log(e)})})}function addToRestaurantOS(e){dbPromise.then(t=>{const r=t.transaction("restaurants","readwrite");return r.objectStore("restaurants").put(e,"restaurants"),r.complete}).then(()=>{}).catch(e=>{console.log(e)})}function getFromRestaurantOS(){return new Promise(function(e,t){dbPromise.then(e=>{return e.transaction("restaurants","readwrite").objectStore("restaurants").get("restaurants")}).then(r=>{r?e(r):t("no data")}).catch(e=>{t("no data"),console.log(e)})})}